<script>
    import { bus } from './../main';
    import { TweenMax, Elastic, Power1 } from 'greensock'

    export default {
        name: 'otter',
        data () {
            return {
                speech: 'You otter look at this portfolio.',
                pageScrolled: false,
                stopAnimation: false
            }
        },
        methods: {
            showOtter() {
                var otter = document.getElementById('otter'),
                    speech = '',
                    text = document.getElementById('text'),
                    tlAnimation = new TimelineMax();

                if (this.pageScrolled === false) {
                    speech = document.getElementById('speech');
                }

                else {
                    speech = document.getElementById('speech-scrolled');
                }

                if (this.stopAnimation === false) {
                    tlAnimation.set(otter, {yPercent: 0});
                    tlAnimation.to(otter, 1.5, {opacity: 1, yPercent: -65, ease: Elastic.easeOut.config(1.15, 0.3), repeat: 0}, 'otter');
                    tlAnimation.to(speech, .5, {opacity: 1, ease: Power1.easeInOut, delay: .5, repeat: 0}, 'otter');
                    tlAnimation.to(text, .5, {opacity: 1, ease: Power1.easeInOut, delay: .5, repeat: 0}, 'otter');
                    this.stopAnimation = true;
                }
            }
        },
        created() {
            window.addEventListener('scroll', this.showOtter);
            bus.$on('setOtterText', (text) => {
                this.speech = text;
                this.pageScrolled = true;
            });
        },
        destroyed () {
          window.removeEventListener('scroll', this.showOtter);
        }
    }
</script>

<style lang="scss" scoped>

    @import "./../scss/main.scss";

    #otter,
    #speech,
    #speech-scrolled,
    #text {
        opacity: 0;
    }

    #text {
        @include font-bold;
        font-size: 1em;

        @supports (font-variation-settings: normal) {
            @include font-600;
        }
    }
</style>
